import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import LoadingSpinner from '../components/LoadingSpinner';

interface CompetitionEntry {
  id: string;
  teamName: string;
  projectName: string;
  description: string;
  features: string[];
  techStack: string[];
  demoUrl?: string;
  githubUrl?: string;
  votes: number;
  hasVoted: boolean;
}

export const CompetitionPage: React.FC = () => {
  const [entries, setEntries] = useState<CompetitionEntry[]>([]);
  const [loading, setLoading] = useState(true);
  const [selectedEntry, setSelectedEntry] = useState<CompetitionEntry | null>(null);
  const [timeLeft, setTimeLeft] = useState<string>('');

  // Mock competition data - trong thực tế sẽ fetch từ API
  useEffect(() => {
    const mockEntries: CompetitionEntry[] = [
      {
        id: '1',
        teamName: 'AI4Life Champions',
        projectName: 'Smart Career Advisor',
        description: 'Hệ thống tư vấn nghề nghiệp thông minh sử dụng AI để phân tích sở thích và khả năng của sinh viên.',
        features: [
          'AI Mentor System với phân tích tâm lý',
          'Gamification với achievement system', 
          'Personalized learning path',
          'Real-time chat với contextual responses',
          'QR Code sharing và PDF export'
        ],
        techStack: ['React', 'TypeScript', 'Node.js', 'OpenAI', 'SQLite', 'Tailwind CSS'],
        demoUrl: 'http://localhost:3000',
        githubUrl: 'https://github.com/ai4life/career-advisor',
        votes: 127,
        hasVoted: false
      },
      {
        id: '2', 
        teamName: 'Tech Innovators',
        projectName: 'EduPath Finder',
        description: 'Ứng dụng tìm kiếm lộ trình học tập phù hợp với từng cá nhân.',
        features: [
          'Machine learning recommendation',
          'Social learning community',
          'Progress tracking',
          'Mentor matching'
        ],
        techStack: ['Vue.js', 'Python', 'TensorFlow', 'PostgreSQL'],
        votes: 89,
        hasVoted: false
      },
      {
        id: '3',
        teamName: 'Future Builders',
        projectName: 'SkillBridge AI',
        description: 'Nền tảng kết nối sinh viên với cơ hội thực tập và việc làm.',
        features: [
          'AI-powered job matching',
          'Skill assessment tools',
          'Company collaboration',
          'Virtual interview practice'
        ],
        techStack: ['Angular', 'Java Spring', 'MongoDB', 'Docker'],
        votes: 156,
        hasVoted: false
      }
    ];

    setTimeout(() => {
      setEntries(mockEntries);
      setLoading(false);
    }, 1000);

    // Countdown timer
    const targetDate = new Date('2024-12-31T23:59:59');
    const timer = setInterval(() => {
      const now = new Date().getTime();
      const distance = targetDate.getTime() - now;

      if (distance > 0) {
        const days = Math.floor(distance / (1000 * 60 * 60 * 24));
        const hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
        const minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
        const seconds = Math.floor((distance % (1000 * 60)) / 1000);

        setTimeLeft(`${days}d ${hours}h ${minutes}m ${seconds}s`);
      } else {
        setTimeLeft('Đã kết thúc');
      }
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  const handleVote = async (entryId: string) => {
    // Simulate API call
    setEntries(prev => prev.map(entry => 
      entry.id === entryId 
        ? { ...entry, votes: entry.votes + 1, hasVoted: true }
        : entry
    ));
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="flex justify-center items-center h-96">
          <LoadingSpinner />
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />
      
      {/* Competition Header */}
      <div className="bg-gradient-to-r from-purple-600 to-blue-600 text-white py-16">
        <div className="container mx-auto px-4 text-center">
          <h1 className="text-4xl md:text-6xl font-bold mb-4">
            🏆 AI4Life Competition
          </h1>
          <p className="text-xl mb-6">
            Cuộc thi ứng dụng AI trong giáo dục - FPT Polytechnic
          </p>
          <div className="bg-white/20 rounded-lg p-4 inline-block">
            <p className="text-lg font-semibold">Thời gian còn lại:</p>
            <p className="text-2xl font-mono">{timeLeft}</p>
          </div>
        </div>
      </div>

      {/* Competition Info */}
      <div className="container mx-auto px-4 py-8">
        <div className="bg-white rounded-xl shadow-lg p-6 mb-8">
          <h2 className="text-2xl font-bold mb-4 flex items-center">
            📋 Tiêu chí đánh giá
          </h2>
          <div className="grid md:grid-cols-2 gap-6">
            <div>
              <h3 className="font-semibold text-purple-600 mb-2">🔧 Chức năng ứng dụng (30đ)</h3>
              <p className="text-gray-600 text-sm">
                Tích hợp AI nhất 1 tính năng AI hoạt động thực tế, giao diện thân thiện, ứng dụng mạng xã hội trí thực tế
              </p>
            </div>
            <div>
              <h3 className="font-semibold text-blue-600 mb-2">📊 Tiêu chí chấm (20đ)</h3>
              <p className="text-gray-600 text-sm">
                Coding, UI/UX, tính hiệu quả, ứng dụng mạng xã hội giải trí thực tế, thuyết trình
              </p>
            </div>
            <div>
              <h3 className="font-semibold text-green-600 mb-2">🎯 Cách thức tổ chức (30đ)</h3>
              <p className="text-gray-600 text-sm">
                Thời gian dự kiến (dự kiến 29/6 - 2/7/2024), mỗi nhóm nhận đề bài riêng 4-6 tiếng buổi sáng thị, chiều mỗi nhóm trình bày 5-7 phút, trả lời Q&A từ BGK
              </p>
            </div>
            <div>
              <h3 className="font-semibold text-orange-600 mb-2">🏅 Vote & BGK (20đ)</h3>
              <p className="text-gray-600 text-sm">
                Trung bày: Sân phẩm gồm máy tính chạy thử nghiệm, poster A1-A2 giới thiệu ứng dụng, chấm điểm: Kết hợp điểm BGK và khán giả đề xác định giải thưởng
              </p>
            </div>
          </div>
        </div>

        {/* Entries Grid */}
        <div className="grid lg:grid-cols-2 gap-8">
          {entries.map((entry) => (
            <div key={entry.id} className="bg-white rounded-xl shadow-lg overflow-hidden hover:shadow-xl transition-shadow">
              {/* Entry Header */}
              <div className="bg-gradient-to-r from-purple-500 to-blue-500 text-white p-6">
                <div className="flex justify-between items-start mb-2">
                  <h3 className="text-xl font-bold">{entry.projectName}</h3>
                  <div className="flex items-center space-x-2">
                    <span className="text-2xl">❤️</span>
                    <span className="font-bold">{entry.votes}</span>
                  </div>
                </div>
                <p className="text-purple-100">by {entry.teamName}</p>
              </div>

              {/* Entry Content */}
              <div className="p-6">
                <p className="text-gray-600 mb-4">{entry.description}</p>

                {/* Features */}
                <div className="mb-4">
                  <h4 className="font-semibold text-gray-800 mb-2">✨ Tính năng nổi bật:</h4>
                  <ul className="space-y-1">
                    {entry.features.map((feature, index) => (
                      <li key={index} className="text-sm text-gray-600 flex items-start">
                        <span className="text-green-500 mr-2">•</span>
                        {feature}
                      </li>
                    ))}
                  </ul>
                </div>

                {/* Tech Stack */}
                <div className="mb-4">
                  <h4 className="font-semibold text-gray-800 mb-2">🛠️ Công nghệ:</h4>
                  <div className="flex flex-wrap gap-2">
                    {entry.techStack.map((tech, index) => (
                      <span key={index} className="bg-gray-100 text-gray-700 px-3 py-1 rounded-full text-sm">
                        {tech}
                      </span>
                    ))}
                  </div>
                </div>

                {/* Actions */}
                <div className="flex justify-between items-center pt-4 border-t">
                  <div className="flex space-x-3">
                    {entry.demoUrl && (
                      <a 
                        href={entry.demoUrl} 
                        target="_blank" 
                        rel="noopener noreferrer"
                        className="text-blue-600 hover:text-blue-800 font-medium text-sm flex items-center"
                      >
                        🚀 Demo
                      </a>
                    )}
                    {entry.githubUrl && (
                      <a 
                        href={entry.githubUrl} 
                        target="_blank" 
                        rel="noopener noreferrer"
                        className="text-gray-600 hover:text-gray-800 font-medium text-sm flex items-center"
                      >
                        📝 Code
                      </a>
                    )}
                  </div>
                  
                  <button
                    onClick={() => handleVote(entry.id)}
                    disabled={entry.hasVoted}
                    className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                      entry.hasVoted
                        ? 'bg-gray-200 text-gray-500 cursor-not-allowed'
                        : 'bg-gradient-to-r from-purple-500 to-blue-500 text-white hover:from-purple-600 hover:to-blue-600'
                    }`}
                  >
                    {entry.hasVoted ? '✅ Đã vote' : '❤️ Vote'}
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Competition Rules */}
        <div className="bg-gradient-to-r from-yellow-50 to-orange-50 rounded-xl p-6 mt-8">
          <h2 className="text-2xl font-bold mb-4 flex items-center text-orange-800">
            📜 Quy định cuộc thi
          </h2>
          <div className="grid md:grid-cols-2 gap-4 text-sm">
            <div>
              <h3 className="font-semibold text-orange-700 mb-2">🎯 Yêu cầu:</h3>
              <ul className="space-y-1 text-orange-600">
                <li>• Ứng dụng phải có tính năng AI thực tế</li>
                <li>• Giao diện thân thiện, dễ sử dụng</li>
                <li>• Có ứng dụng thực tế trong giáo dục</li>
                <li>• Code clean và có documentation</li>
              </ul>
            </div>
            <div>
              <h3 className="font-semibold text-orange-700 mb-2">🏆 Giải thưởng:</h3>
              <ul className="space-y-1 text-orange-600">
                <li>• Giải Nhất: 10,000,000 VNĐ + Cúp</li>
                <li>• Giải Nhì: 5,000,000 VNĐ + Cúp</li>
                <li>• Giải Ba: 3,000,000 VNĐ + Cúp</li>
                <li>• Giải Khuyến khích: 1,000,000 VNĐ</li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CompetitionPage;
